<?php    /**     * Created by PhpStorm.     * User: user     * Date: 26.01.2017     * Time: 17:34     */    class Info{        public static function get(){            @session_start();            if(isset($_SESSION['resPid']) || isset($_SESSION['resSas'])){                $pid = $_SESSION['resPid'];                $getInfo = \StaticGame\StaticGame::sql("SELECT account_id FROM player.player WHERE id = ?",[$pid]);                $result['aid'] = $getInfo[0]['account_id'];                $result['pid'] = $pid;            }elseif(isset($_GET['token'])){                $getToken = filter_var($_GET['token'],FILTER_SANITIZE_URL);                $decrypt = inDecrypt($getToken);                $sub = explode('/',$decrypt);                $result['pid'] = $sub[0];                $result['aid'] = $sub[2];            }elseif(isset($_GET['pid']) || isset($_GET['sas'])){                $pid = filter_var($_GET['pid'],FILTER_SANITIZE_URL);                $getInfo = \StaticGame\StaticGame::sql("SELECT account_id FROM player.player WHERE id = ?",[$pid]);                $result['pid'] = $pid;                $result['aid'] = $getInfo[0]['account_id'];            }            return @$result;        }        public  function totalYang($account_id){            $sth = \StaticGame\StaticGame::sql("SELECT SUM(gold) as toplam FROM player.player WHERE account_id = ?",[$account_id])[0];            return $sth['toplam'];        }    }