									function BaslikVeri()
									{
										width = document.body.clientWidth;
										if(width < 1800)
										{
											document.getElementById("BaslikVeri1").style.display = "none";
											document.getElementById("BaslikVeri2").style.display = "none";
										}
										else
										{
											document.getElementById("BaslikVeri1").style.display = "block";
											document.getElementById("BaslikVeri2").style.display = "block";
										}
									}

									var coll = document.getElementsByClassName("collapsible");
									var i;

									for (i = 0; i < coll.length; i++) {
										coll[i].addEventListener("click", function() {
											this.classList.toggle("active");
											var content = this.nextElementSibling;
											if (content.style.display === "block") {
												content.style.display = "none";
											} else {
												content.style.display = "block";
											}
										});
									}
								</script>
								
